sys_script_include.script:
var GetMembers = Class.create();
GetMembers.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getMembers: function() {
        var gr = new GlideRecord('x_824599_treat_man_group');
		var gn = this.getParameter('sysparm_groupName');
        gr.get('group_name', gn);
		if(gn != '' && gr.number == undefined) {
			gs.addErrorMessage("No such group exists");
		}
        return gr.u_members;
    },
    type: 'GetMembers'
});
